import React, { useState } from "react";
import "../styles/form.css";
export default function Form(){
  const[formData, setFormData]= useState({ //Holds values for all form fields as one object
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    company: "",
    projectDetails: "",
  });
  const[submitted, setSubmitted] = useState(false); //Tracks whether the form was submitted to show a success message.
//handling change value[hc]
  const hc =(e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value }); //mapping vallues
  };
  //handling submited value[hs]
  const hs= (e) => {
    e.preventDefault();
    console.log("Form submitted:", formData);
    setSubmitted(true);
    //reset form once filled
    setFormData({
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      company: "",
      projectDetails: "",
    });
  };
  return(
    <section id="form" className="form">
      <h2>Let's Start Your Training Journey</h2>
      <div className="formc">
        <form className="box" onSubmit={hs}>
          <input
            type="text"
            name="firstName"
            placeholder="First Name*"
            required
            value={formData.firstName}
            onChange={hc}
          />
          <input
            type="text"
            name="lastName"
            placeholder="Last Name"
            value={formData.lastName}
            onChange={hc}
          />
          <input
            type="email"
            name="email"
            placeholder="Email Address*"
            required
            value={formData.email}
            onChange={hc}
          />
          <input
            type="tel"
            name="phone"
            placeholder="Phone Number"
            value={formData.phone}
            onChange={hc}
          />
          <input
            type="text"
            name="company"
            placeholder="Company Name"
            value={formData.company}
            onChange={hc}
          />
          <textarea
            name="projectDetails"
            placeholder="Project Details*"
            required
            value={formData.projectDetails}
            onChange={hc}
          ></textarea>
          <button type="submit">Send Message</button>
        </form>

        {submitted &&(
          <p className="msg">Your message has been submitted successfully!</p>
        )}

        <div className="details">
          <h3>Contact Info</h3>
          <p><strong>Email:</strong>training@intellipaat.in</p>
          <p><strong>Phone:</strong>+91 6060660444</p>

          <h3>Office Address</h3>
          <p>Remarks on the Training Available</p>
          <p>Global Reach</p>

          <h3>Social Media</h3>
          <div className="social">
            <span><img src="/linkedin.png" alt="linkedlin"></img></span>
            <span><img src="/facebook.png" alt="Facebook"></img></span>
            <span><img src="/twitter.png" alt="twitter"></img></span>
            <span><img src="/instagram.png" alt="insta"></img></span>
          </div>
        </div>
      </div>
    </section>
  );
}
