import React, { useState } from "react";
import "../styles/Form.css";

export default function Form() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    company: "",
    projectDetails: "",
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Just show success message, no backend call
    console.log("Form submitted:", formData);
    setSubmitted(true);

    // Reset form
    setFormData({
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      company: "",
      projectDetails: "",
    });
  };

  return (
    <section className="form">
      <h2>Let's Start Your Training Journey</h2>
      <div className="form-container">
        <form className="form-box" onSubmit={handleSubmit}>
          <input
            type="text"
            name="firstName"
            placeholder="First Name*"
            required
            value={formData.firstName}
            onChange={handleChange}
          />
          <input
            type="text"
            name="lastName"
            placeholder="Last Name"
            value={formData.lastName}
            onChange={handleChange}
          />
          <input
            type="email"
            name="email"
            placeholder="Email Address*"
            required
            value={formData.email}
            onChange={handleChange}
          />
          <input
            type="tel"
            name="phone"
            placeholder="Phone Number"
            value={formData.phone}
            onChange={handleChange}
          />
          <input
            type="text"
            name="company"
            placeholder="Company Name"
            value={formData.company}
            onChange={handleChange}
          />
          <textarea
            name="projectDetails"
            placeholder="Project Details*"
            required
            value={formData.projectDetails}
            onChange={handleChange}
          ></textarea>
          <button type="submit">Send Message</button>
        </form>

        {submitted && (
          <p className="success-message">‚úÖ Your message has been submitted successfully!</p>
        )}

        <div className="form-info">
          <h3>Contact Info</h3>
          <p><strong>Email:</strong> training@intellipaat.in</p>
          <p><strong>Phone:</strong> +91 6060660444</p>

          <h3>Office Address</h3>
          <p>Remarks on the Training Available</p>
          <p>Global Reach</p>

          <h3>Social Media</h3>
          <div className="social-icons">
            <span>üìò</span>
            <span>üê¶</span>
            <span>üíº</span>
            <span>üì∏</span>
          </div>
        </div>
      </div>
    </section>
  );
}
