import React from "react";

const services = [
  { title: "Instructor-Led Training", desc: "Interactive classroom sessions with expert trainers." },
  { title: "Virtual Training", desc: "Flexible online training programs anywhere in the world." },
  { title: "Self-Paced Learning", desc: "On-demand video lessons with progress tracking." }
];

export default function Services() {
  return (
    <section id="services" className="services">
      <h2>Our Services</h2>
      <div className="service-grid">
        {services.map((s, i) => (
          <div key={i} className="service-card">
            <h3>{s.title}</h3>
            <p>{s.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
