import React from "react";
import "../styles/pjourney.css";

export default function PJourney() {
  const journeySteps = [
    {
      title: "Requirement Discovery",
      description: "We begin by understanding your organization‚Äôs goals, challenges, and learning needs.",
      icon: "üîç",
    },
    {
      title: "Department Alignment",
      description: "We collaborate across departments to ensure training aligns with business priorities.",
      icon: "ü§ù",
    },
    {
      title: "Learner Insight",
      description: "We analyze learner profiles to tailor content for engagement and relevance.",
      icon: "üß†",
    },
    {
      title: "Tailored Program Design",
      description: "We craft customized learning journeys based on roles, goals, and delivery preferences.",
      icon: "üé®",
    },
  ];

  const partnershipBenefits = [
    "Extension of Your L&D Team",
    "Outcome-Focused Approach",
    "Engaging, Practical Learning Experiences",
    "Customized, Role-Based Learning Journeys",
    "Industry-Sourced Insights & Best Practices",
    "Scalable, Hybrid Delivery",
    "End-to-End Partnership Support",
  ];

  const moreReasons = [
    "Personalized",
    "Quality",
    "Fast Implementation",
    "Dedicated Support",
    "Flexible",
    "Scalable",
  ];

  return (
    <section className="pjourney">
      <h2>Our Partnership Journey</h2>
      <p>We co-create impactful learning programs through a structured, empathetic approach.</p>

      <div className="journey-grid">
        {journeySteps.map((step, index) => (
          <div key={index} className="journey-card">
            <div className="icon">{step.icon}</div>
            <h3>{step.title}</h3>
            <p>{step.description}</p>
          </div>
        ))}
      </div>

      <div className="benefits-section">
        <h3>Key Partnership Benefits</h3>
        <ul>
          {partnershipBenefits.map((item, index) => (
            <li key={index}>‚úÖ {item}</li>
          ))}
        </ul>
      </div>

      <div className="reasons-section">
        <h3>More Reasons to Partner With Us</h3>
        <div className="reasons-grid">
          {moreReasons.map((reason, index) => (
            <div key={index} className="reason-box">{reason}</div>
          ))}
        </div>
      </div>
    </section>
  );
}
