import React, { useState } from "react";
import "../styles/Domains.css";

export default function Domains() {
  const [selectedDomain, setSelectedDomain] = useState(null);

  const arr = [
    {
      name: "Technology",
      description:
        "Master the latest in software development, cloud computing, cybersecurity, and digital transformation.",
      courses: 29,
      topics: [
        "Web Development",
        "Cybersecurity",
        "DevOps & CI/CD",
        "AI & Machine Learning",
        "Cloud Computing",
        "Data Science",
      ],
      highlights: [
        "Hands-on projects & real world scenarios",
        "Industry expert instructors",
        "Latest tools & frameworks",
      ],
      duration: "8-12 weeks per course",
    },
    {
      name: "Marketing",
      description:
        "Grow your skills with cutting-edge digital marketing, brand management, and consumer analytics courses.",
      courses: 21,
      topics: [
        "Content Marketing",
        "SEO & SEM",
        "Social Media Marketing",
        "Brand Management",
        "Digital Analytics",
        "Consumer Behavior",
      ],
      highlights: [
        "Case studies & industry tools",
        "Expert-led sessions",
        "Practical assignments",
      ],
      duration: "8-12 weeks per course",
    },
    // ... other domains
  ];

  // If a domain is selected, show only that one
  const domainsToShow = selectedDomain
    ? arr.filter((domain) => domain.name === selectedDomain)
    : arr;

  return (
    <section id="domains" className="domains">
      <h5>Our Domains</h5>
      <h2>Expert Training Across Six Key Domains</h2>
      <p>
        Our programs are designed and delivered by industry experts to ensure
        your team gains practical, relevant skills.
      </p>

      <div className="domstr">
        {domainsToShow.map((domain, index) => (
          <div key={index} className="dombox">
            <h3>{domain.name}</h3>
            <p>{domain.description}</p>
            <p>
              <strong>{domain.courses}+ Courses Available</strong>
            </p>

            <div className="dsec">
              <h4>Core Topics</h4>
              <ul>
                {domain.topics.map((topic, i) => (
                  <li key={i}>{topic}</li>
                ))}
              </ul>
            </div>

            <div className="dsec">
              <h4>Program Highlights</h4>
              <ul>
                {domain.highlights.map((item, i) => (
                  <li key={i}>{item}</li>
                ))}
              </ul>
            </div>

            <p>
              <strong>Duration:</strong> {domain.duration}
            </p>

            <button
              className="ebtn"
              onClick={() => setSelectedDomain(domain.name)}
            >
              Explore Courses →
            </button>
          </div>
        ))}
      </div>

      {/* Optional: Add a back button to show all domains again */}
      {selectedDomain && (
        <button className="backbtn" onClick={() => setSelectedDomain(null)}>
          ← Back to All Domains
        </button>
      )}
    </section>
  );
}
